var Pool = require('mysqlpool'), pool = new Pool({url: 'mysql://root:root@127.0.0.1/employees', connections: 30});

var http = require('http');
http.createServer(function (req, res) {
	res.writeHead(200, {'Content-Type': 'text/plain'});
	pool.getClient().query("select * from employees Limit 30", function(err, users){
		if(err)
			throw err;
		res.end('');
	});
}).listen(1337, '192.168.1.3');