var mysql = require('mysql'), connection = mysql.createConnection('mysql://root:root@127.0.0.1/employees');
connection.connect();
var http = require('http');
http.createServer(function (req, res) {
	res.writeHead(200, {'Content-Type': 'text/plain'});
	connection.query("select * from employees Limit 30", function(err, users){
		if(err)
			throw err;
		res.end('');
	});
}).listen(1338, '192.168.1.3');